/* eslint-disable react/jsx-no-comment-textnodes */
import React, { Component } from "react";
import { FormInput } from "../../../components/form/form";
import style from "./Login.module.scss";
import log from "../../img/log.svg";
import * as Yup from "yup";
import { Formik, Form } from "formik";
import { Link } from "react-router-dom";
import axios from "axios";
import { apiAccount } from "../../../services/URL/constURL";
import { checkObject } from "../../../utils/compareObject";
class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      form: {
        email: "",
        password: "",
      },
      account: [],
      check: false,
    };
  }

  componentDidMount() {
    axios.get(apiAccount).then((res) => {
      const persons = res.data;
      this.setState({ account: persons });
    });
  }

  render() {
    const user = this.state.account;
    return (
      <div className={style.loginIdentity}>
        <div className={style.loginLeft}>
          <div className={style.title}>
            <h1>New Here ?</h1>
            <h2>Sign out of Past.. Login to the new!</h2>
            <div className={style.btnSignUp}>
              <Link to="./register">
                <button>SIGN UP</button>
              </Link>
            </div>
          </div>
        </div>
        <div className={style.footer}>
          <img src={log} alt="" />
        </div>
        <div className={style.loginRight}>
          <div className={style.LoginForm}>
            <h1>SIGN IN</h1>
            <div className={style.LoginFormik}>
              <Formik
                initialValues={this.state.form}
                validationSchema={Yup.object({
                  email: Yup.string()
                    .required("Required"),
                  password: Yup.string()
                    .min(8, "Minimum 8 characters")
                    .required("Required!")
                    .matches(/(?=.*[0-9])/, "Password must contain a number.")
                })}
                validate={values => {
                  let error ={};
                  if(checkObject(values, user) === false) {
                    if(values.email = '') error.email = "Invalid email addresss";
                    else error.email = "The email doesn't exist";
                  }
                  return error;
                }}
                onSubmit={async (values, { setSubmitting }) => {
                  await new Promise((r) => setTimeout(r, 500));
                  setSubmitting(false);
                  console.log(values);
                  this.setState({ check: checkObject(values, user) });
                  console.log(values.error);

                }}
              >
                <Form>
                  <FormInput
                    label="Email Address"
                    name="email"
                    type="email"
                    placeholder="TMA@formik.com"
                  />
                  <FormInput
                    label="Password"
                    name="password"
                    type="password"
                    placeholder="Your Password"
                  />
                  <div className={style.btnLogin}>
                    {this.state.check ? (
                      <button type="submit">
                        <Link to="/category">LOGIN</Link>
                      </button>
                    ) : (
                      <button type="submit">LOGIN</button>
                    )}
                  </div>
                </Form>
              </Formik>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
