export const checkObject = (person, account) => {
  let check = true;

  // eslint-disable-next-line array-callback-return
  account.filter((user) => {
    return Object.keys(person).every((account) => {
      return person[account] === user[account]
        ? (check = true)
        : (check = false);
    });
  });

  return check;
};

export const convertObject = (value, item) => {
  // eslint-disable-next-line no-template-curly-in-string
  const array = array.push(value);
  Object.assign(...value.map(el => ({"`${item}`" : el})))
}
