/* eslint-disable react/jsx-no-comment-textnodes */
import React, { Component } from "react";
import { FormInput } from "../../../components/form/form";
import style from "./Login.module.scss";
import log from "../../img/log.svg";
import * as Yup from "yup";
import { Formik, Form } from "formik";
import { Link } from "react-router-dom";
import axios from "axios";
import { apiAccount } from "../../../services/URL/constURL";
import { checkObject } from "../../../utils/compareObject";
import { message, Alert} from "antd";
import 'antd/dist/antd.css';
class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      form: {
        email: "",
        password: "",
      },
      account: [],
      check: false,
    };
    this.success = this.success.bind(this);
    this.error = this.error.bind(this);
  }

  componentDidMount() {
    axios.get(apiAccount).then((res) => {
      const persons = res.data;
      this.setState({ account: persons });
    });
  }

  success = (person) => {
    message.success(`Welcome to ${person}`);
  };

  error = () => {
    message.error('Something is wrong, Please check again ablow!');

  };

  render() {
    const user = this.state.account;
    console.log(user);
    return (
      <div className={style.loginIdentity}>
        <div className={style.loginLeft}>
          <div className={style.title}>
            <h1>New Here ?</h1>
            <h2>Sign out of Past.. Login to the new!</h2>
            <div className={style.btnSignUp}>
              <Link to="./register">
                <button>SIGN UP</button>
              </Link>
            </div>
          </div>
        </div>
        <div className={style.footer}>
          <img src={log} alt="" />
        </div>
        <div className={style.loginRight}>
          <div className={style.LoginForm}>
            <h1>SIGN IN</h1>
            <div className={style.LoginFormik}>
              <Formik
                initialValues={this.state.form}
                validationSchema={Yup.object({
                  email: Yup.string().required("Required"),
                  password: Yup.string()
                    .min(8, "Minimum 8 characters")
                    .required("Required!")
                    .matches(/(?=.*[0-9])/, "Password must contain a number."),
                })}
                validate={(values) => {
                  const EmailValidator =
                    /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
                  let error = {};
                  // if(values.email.length === 0) error.email = "Required";
                  if (checkObject(values, user) === false)
                    error.email = "The email doesn't exist";
                  if (!EmailValidator.test(values.email))
                    error.email = "Invalid email address.";

                  return error;
                }}
                onSubmit={async (values, { setSubmitting }) => {
                  setTimeout(() => {
                    console.log("Logging in", values);
                    setSubmitting(false);
                    console.log(values.email);
                  }, 500);

                  // setSubmitting(false);
                  // console.log(values);
                  Promise(this.setState({ check: checkObject(values, user) }));
                  // console.log(this.validat e(values.error.email));
                }}
              >
                 {({ values }) => (
                <Form>
                  <FormInput
                    label="Email Address"
                    name="email"
                    type="email"
                    placeholder="TMA@formik.com"
                  />
                  <FormInput
                    label="Password"
                    name="password"
                    type="password"
                    placeholder="Your Password"
                  />
                  <div className={style.btnLogin}>
                    {this.state.check ? (
                      <Link to="/category">
                        <button onClick={this.success(values.email)} type="submit">LOGIN</button>
                      </Link>
                    ) : (
                      <button type="submit" onClick={this.error}>LOGIN</button>
                    )}
                  </div>
                </Form>
                 )}
              </Formik>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
